*::before {
    padding: 0;
    margin: 0;
    border: 0;
    box-sizing: border-box;
}

a {
    text-decoration: none;
    transition: .5s;
    animation-timing-function: ease-in-out;
    color: inherit;
}

a:hover {
    color: inherit;
    text-decoration: none;
}

ol,
ul,
li {
    list-style: none;
    margin: 0;
}

html,
body {
    height: 100%;
    margin: 0;
}

body {
    font-family: 'Nunito', sans-serif;
    font-size: 20px;
    color: #1C1E23;
    line-height: 150%;
}

.page-wrapper {
    min-height: 100%;
    display: flex;
    flex-direction: column;
}

.main {
    flex: 1 1 auto;
}

h3 {
    text-align: center;
    font-family: 'Cormorant Garamond', serif;
    color: #1C1E23;
    letter-spacing: 0.05em;
    text-transform: uppercase;
    font-weight: 500;
    font-size: 70px;
    line-height: 130%;
    margin-bottom: 63px;
}

h3 span {
    text-transform: capitalize;
    color: #F4D99C;
}

img {
    max-width: 100%;
}

/* header */
.header {
    padding: 0 35px;
    height: 110px;
    background: #17302D;
    position: fixed;
    width: 100%;
    left: 0;
    top: 0;
    z-index: 11;
}

.header__logo {
    flex: 0 0 190px;
    max-width: 190px;
    width: 100%;
}

.header__right {
    flex: 0 0 425px;
    max-width: 425px;
    width: 100%;
}

.header__nav {
    flex: 0 0 calc(100% - 615px);
    max-width: calc(100% - 615px);
    width: 100%;
    padding-left: 50px;
}

.header__logo img {
    max-width: 100%;
}

.header__nav ul li {
    display: inline-block;
    vertical-align: middle;
    margin-right: 50px;
}

.header__nav ul li:last-child {
    margin-right: 0;
}

.header__nav ul li a {
    text-transform: uppercase;
    color: #fff;
    letter-spacing: 0.06em;
    font-weight: 600;
    font-size: 18px;
    line-height: 130%;
}

.header__nav ul li a:hover {
    color: #F4D99C;
}

.header__right {
    display: flex;
    align-items: center;
    justify-content: space-between;
}

.header__language ul li {
    display: inline-block;
    vertical-align: middle;
    margin-right: 8px;
}

.header__language ul li a {
    text-transform: uppercase;
    color: #42837B;
    font-size: 18px;
    line-height: 25px;
}

.header__language ul li.active a {
    color: #fff;
}

.header__language ul li:last-child {
    margin-right: 0;
}

.header__language ul li a:hover {
    color: #fff;
}

.header__tel ul {
    display: none;
}

.header>.row {
    height: 100%;
}

.header__tel>a {
    display: inline-block;
    padding: 0 36px;
    letter-spacing: 0.06em;
    color: #fff;
    position: relative;
}

.header__tel>a:hover {
    color: #F4D99C;
}


.header__tel>a:after {
    display: inline-block;
    content: '';
    width: 25px;
    height: 12px;
    background: url('../img/svg/arrow-icon.svg') no-repeat center / 100%;
    position: absolute;
    right: 0;
    top: 50%;
    transform: translateY(-50%);
}

.header__tel>a:before {
    display: inline-block;
    content: '';
    width: 21px;
    height: 21px;
    background: url('../img/svg/tel-icon.svg') no-repeat center / 100%;
    position: absolute;
    left: 0;
    top: 50%;
    transform: translateY(-50%);
}



/* header end */

